mapper.delete:
delete
from UserLiterature
where userID = #{userID} and literatureID = #{literatureID}

mapper.select:
select count(*)
from UserLiterature
where literatureID = #{literatureID} and PERIOD_DIFF(date_format(now(), '%Y%m'), date_format(collectTime, '%Y%m')) = #{diff}

select userID
from UserLiterature
where literatureID = #{literatureID} and userID = #{userID}
