mapper.select:
select username
from user
where username = #{username}

mapper.update:
update user
set username = #{username}, realName = #{realName}, userDegree = #{userDegree}, image = #{image}, emailAddress = #{emailAddress}, phoneNumber = #{phoneNumber}, organization = #{organization}
where userID = #{userID}

mapper.select:
select user.userID, userIdentity, username, realName, image, userDegree, organization, emailAddress, phoneNumber
from user,follow
where follow.userID = user.userID and followerID = #{userID}

select user.userID, userIdentity, username, realName, image, userDegree, organization, emailAddress, phoneNumber
from user,follow
where followerID = user.userID and follow.userID = #{userID}

mapper.delete:
delete
from follow
where followerID = #{followerID} and userID = #{userID}
